<?php/** * * Template Name: Page ID Based Multi Template * @link https://developer.wordpress.org/themes/basics/template-hierarchy/ * @package garbott_light */get_header();?>    <div id="primary" class="content-area">        <main id="main" class="site-main">            <?php            $page_id = get_queried_object_id();            if ($page_id == 7) { // Change number to match page ID for Home                get_template_part('template-parts/content', 'home');            } elseif ($page_id == 11) {                get_template_part('template-parts/content', 'new-page');            } elseif ($page_id == 14) {                get_template_part('template-parts/content', 'test-page');            } else {                get_template_part('template-parts/content', 'page'); //Default Template            }            ?>        </main><!-- #main -->    </div><!-- #primary --><?phpget_footer();